currtim=currtim.getHours();

let greeting="";
const cssStyle={};

if(currtim>1 && currtim<12){
    greeting="GOOD MORNING";
    cssStyle.color="Green";
}else if(currtim>=12 && currtim<=19){
    greeting="GOOD EVENING";
    cssStyle.color="Orange";
}else{
    greeting="GOOD NIGHT";
    cssStyle.color="Black";
}

    return(
         <div><h1> Hello Stark</h1></div>
    );
}

