import React from 'react';

function App(){
    let currtim=new Date();
    currtim=currtim.getHours();

    let greeting="";
    const cssStyle={};

    if(currtim>1 && currtim<12){
        greeting="GOOD MORNING";
        cssStyle.color="Green";
    }else if(currtim>=12 && currtim<=19){
        greeting="GOOD EVENING";
        cssStyle.color="Orange";
    }else{
        greeting="GOOD NIGHT";
        cssStyle.color="Black";
    }

    return(
        <>
         <div>
         <h1> Hello Stark, <span style={cssStyle}>{greeting}</span>
         </h1>
         </div>
        </>
    );
}

export default App;
